class Migration_$MIGRATION$ extends CI_Migration {

    public function up() {
        // Deshabilitar las llaver foráneas
        $this->_disable_key_checks();

        $UP$

        // Terminada la migración, volver a habilitar las llaves foráneas
        $this->_enable_key_checks();
    }
    //------------------------------------------------------------------------------------------------------------------
    public function down() {
        // Deshabilitar las llaver foráneas
        $this->_disable_key_checks();

        $DOWN$

        // Terminada la migración, volver a habilitar las llaves foráneas
        $this->_enable_key_checks();
    }
    //------------------------------------------------------------------------------------------------------------------
    private function _add_foreign_key($table, $local_key, $foreign_table, $foreign_key = NULL) {
        $fkTemplate = "ALTER TABLE `%s` ADD FOREIGN KEY(`%s`) REFERENCES %s(`%s`) ON DELETE CASCADE ON UPDATE CASCADE";
        $foreign_key = $foreign_key !== NULL ? $foreign_key : $local_key;
        $fkQuery = sprintf($fkTemplate, $table, $local_key, $foreign_table, $foreign_key);

        $this->db->query($fkQuery);
    }
    //------------------------------------------------------------------------------------------------------------------
    private function _drop_foreign_key($table, $fkIndex = 1) {
        $fkQuery = "ALTER TABLE " . $table . " DROP FOREIGN KEY " . $table . "_ibfk_" . $fkIndex . " ;";

        $this->db->query($fkQuery);
    }
    //------------------------------------------------------------------------------------------------------------------
    private function _enable_key_checks() {
        $enable_key_checks = "SET FOREIGN_KEY_CHECKS = 1;";
        $this->db->query($enable_key_checks);
    }
    //------------------------------------------------------------------------------------------------------------------
    private function _disable_key_checks() {
        $disable_key_checks = "SET FOREIGN_KEY_CHECKS = 0;";
        $this->db->query($disable_key_checks);
    }
}

