class $APINAME$ extends REST_Controller {
    private $userdata;

    function __construct()
    {
        // Construct the parent class
        parent::__construct();


        // Configure limits on our controller methods
        // Ensure you have created the 'limits' table and enabled 'limits' within application/config/rest.php
        $this->methods['user_get']['limit'] = 500; // 500 requests per hour per user/key
        $this->methods['user_post']['limit'] = 100; // 100 requests per hour per user/key
        $this->methods['user_delete']['limit'] = 50; // 50 requests per hour per user/key

        header('Access-Control-Allow-Origin: *');
        header("Access-Control-Allow-Headers: X-API-KEY, Origin, X-Requested-With, Content-Type, Accept, Access-Control-Request-Method");
        header("Access-Control-Allow-Methods: GET, POST, OPTIONS, PUT, DELETE");
        $method = $_SERVER['REQUEST_METHOD'];
        if ($method == "OPTIONS") {
            die();
        }

        $this->userdata = null;

         // El hash JWT generado en Login_post() es pasado a cada petición de cada acción por medio del header Auth. Para
        // recuperarlo, lo primero que hay que hacer es recuperar los headers
        $headers = $this->input->request_headers();

        // Si en los headers (es un array) existe el campo "Auth" y contiene un valor...
        if (array_key_exists('Auth', $headers) && !empty($headers['Auth'])) {
            //... Desencripta el token JWT
            $decodedToken = AUTHORIZATION::validateUser($headers['Auth']);

            // Si el token es desencriptable...
            if ($decodedToken != false) {

                //... Recupera los valores del array con el que se generó
                $this->userdata = $decodedToken->userdata;
            }
        }
        // Si las validaciones fallan, la función no se termina y llega a este punto, mostrando un mensaje de error si
        // el token no es desencriptable o si no existe un header "Auth".
    }
    /*==================================================================================================================
                                            POSIBLES RESPUESTAS HTTP

    $this->response($users, REST_Controller::HTTP_OK); // OK (200)
    $this->response($users, REST_Controller::HTTP_NOT_FOUND); // NOT_FOUND (404)
    $this->response($users, REST_Controller::HTTP_BAD_REQUEST); // BAD_REQUEST (400)
    $this->response($message, REST_Controller::HTTP_CREATED); // CREATED (201) Usar en INSERT
    ==================================================================================================================*/
    //------------------------------------------------------------------------------------------------------------------
    public function index_get()
    {

    }
    //------------------------------------------------------------------------------------------------------------------
    public function secure_index_get()
    {
        // Colocar esta linea en cada controlador validado.
        if (!$this->_session_started()) return;
    }
    //------------------------------------------------------------------------------------------------------------------
    public function index_post()
    {

    }
    //------------------------------------------------------------------------------------------------------------------
    public function index_delete()
    {

    }
    //------------------------------------------------------------------------------------------------------------------
    public function index_put()
    {

    }
    //------------------------------------------------------------------------------------------------------------------
    private function _session_started()
    {
        if ($this->userdata === null) {
            // Respuesta. HTTP_UNAUTHORIZED = 401 (Unauthorized). Respuesta que se da cuando no se puede iniciar sesión
            // con el usuario y la contraseña proporcionadas.
            $this->set_response(array(
                "userdata" => null,
                "error" => "Acceso denegado"
            ), REST_Controller::HTTP_UNAUTHORIZED);
            return false;
        }
        return true;
    }
}

