    {

    }
    //------------------------------------------------------------------------------------------------------------------
    private function _session_started()
    {
        if ($this->userdata === null) {
            // Respuesta. HTTP_UNAUTHORIZED = 401 (Unauthorized). Respuesta que se da cuando no se puede iniciar sesión
            // con el usuario y la contraseña proporcionadas.
            $this->set_response(array(
                "userdata" => null,
                "error" => "Acceso denegado"
            ), REST_Controller::HTTP_UNAUTHORIZED);
            return false;
        }
        return true;
    }
}

